package com.example.demo.Controller;import com.example.demo.VO.SearchPojo;import com.example.demo.service.CombinationService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import org.springframework.stereotype.Controller;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.util.ArrayList;import java.util.List;@Controllerpublic class DemoController {    @Autowired    CombinationService combinationService;    @RequestMapping(value = "/test", method = RequestMethod.GET)    public String mainpage() {        return "main.html";    }    @RequestMapping(value = "/test", method = RequestMethod.POST)    @CrossOrigin("*")    public @ResponseBody List<String> calculation(@RequestBody SearchPojo searchPojo, HttpServletRequest request, HttpServletResponse response) {        String str = searchPojo.getInput();        if (str == null || str.length() == 0) {            return new ArrayList<String>();        }        int index =  Integer.valueOf(searchPojo.getPageIndex());        int size =  Integer.valueOf(searchPojo.getPageSize());        List<String> list = combinationService.letterCombinations(str, index, size);        return list;    }}